package com.br.contactfetchapp.data.mapperimport com.br.contactfetchapp.data.model.RandomUserimport com.br.contactfetchapp.data.source.local.room.entity.RandomUserEntityimport com.br.contactfetchapp.data.source.remote.response.Resultfun Result.toModel(): RandomUser = RandomUser(    fullName = "${this.name.first} " + this.name.last,    gender = this.gender,    location = "${this.location.city} - ${this.location.state}, " + this.location.country,    email = this.email,    picture = this.picture.large,    cell = this.cell,    dob = "${this.dob.age}",    nat = this.nat,    phone = this.phone,    registered = this.registered.date,)fun Result.toEntity(): RandomUserEntity = RandomUserEntity(    fullName = "${this.name.first} " + this.name.last,    gender = this.gender,    location = "${this.location.city} - ${this.location.state}, " + this.location.country,    email = this.email,    picture = this.picture.large,    cell = this.cell,    dob = "${this.dob.age}",    nat = this.nat,    phone = this.phone,    registered = this.registered.date,)fun RandomUserEntity.toModel(): RandomUser = RandomUser(    fullName = this.fullName,    gender = this.gender,    location = this.location,    email = this.email,    picture = this.picture,    cell = this.cell,    dob = this.dob,    nat = this.nat,    phone = this.phone,    registered = this.registered,)