package com.br.contactfetchapp.domain.usecaseimport com.br.contactfetchapp.data.model.RandomUserimport com.br.contactfetchapp.data.repository.RandomUserRepositoryimport kotlinx.coroutines.flow.Flowclass RandomUserUseCaseImpl(    private val repository: RandomUserRepository,) : RandomUserUseCase {    override fun invoke(page: Int, pageSize: Int): Flow<List<RandomUser>> =        repository.getAllUser(page, pageSize)}